import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { FaFileAudio, FaFileImage, FaFileCode, FaFilePdf, FaFileAlt } from 'react-icons/fa'; // Import icons
import '../styles/RepoDetailsPage.css';

const RepoDetailsPage = () => {
  const { repoName } = useParams();

  // Local state to simulate uploaded files
  const [files, setFiles] = useState([]);

  // File type mapping
  const getFileIcon = (fileName) => {
    const extension = fileName.split('.').pop().toLowerCase();

    switch (extension) {
      case 'mp3':
      case 'wav':
      case 'ogg':
        return <FaFileAudio className="file-icon" />;
      case 'png':
      case 'jpg':
      case 'jpeg':
      case 'gif':
        return <FaFileImage className="file-icon" />;
      case 'html':
      case 'css':
      case 'js':
      case 'jsx':
        return <FaFileCode className="file-icon" />;
      case 'pdf':
        return <FaFilePdf className="file-icon" />;
      default:
        return <FaFileAlt className="file-icon" />;
    }
  };

  // Simulate file upload
  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files).map((file) => file.name);
    setFiles((prevFiles) => [...prevFiles, ...uploadedFiles]);
  };

  return (
    <div className="repo-details-page">
      <h1>Repository: {repoName}</h1>

      {/* File upload */}
      <div className="upload-section">
        <input
          type="file"
          multiple
          onChange={handleFileUpload}
          className="file-upload"
        />
      </div>

      {/* File list */}
      <ul className="file-list">
        {files.map((file, index) => (
          <li key={index}>
            {getFileIcon(file)}
            <span className="file-name">{file}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default RepoDetailsPage;
