// src/pages/CreateRepository.js
import React, { useState } from 'react';

const CreateRepository = ({ onCreate }) => {
  const [repoName, setRepoName] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (repoName.trim() !== '') {
      // Simulate API call (you can integrate real API later)
      const newRepo = {
        name: repoName,
        description: description,
        id: Math.random().toString(36).substring(2, 9), // Just a random ID for now
      };
      onCreate(newRepo);
      setRepoName('');
      setDescription('');
    }
  };

  return (
    <div className="create-repository-container">
      <h2>Create New Repository</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Repository Name:
          <input
            type="text"
            value={repoName}
            onChange={(e) => setRepoName(e.target.value)}
            required
          />
        </label>
        <br />
        <label>
          Description:
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Create Repository</button>
      </form>
    </div>
  );
};

export default CreateRepository;
