import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/RepoDetailsPage.css';

const RepoDetailsPage = () => {
  const { repoName } = useParams();

  // Sample files (initial state)
  const [files, setFiles] = useState(['index.html', 'main.css', 'app.js', 'README.md']);
  const [newFileName, setNewFileName] = useState('');
  const [fileUploadMessage, setFileUploadMessage] = useState('');

  const handleCreateFile = () => {
    if (newFileName.trim()) {
      setFiles([...files, newFileName]);
      setNewFileName('');
      setFileUploadMessage(`File "${newFileName}" created successfully!`);
    }
  };

  const handleFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    if (uploadedFile) {
      setFiles([...files, uploadedFile.name]);
      setFileUploadMessage(`File "${uploadedFile.name}" uploaded successfully!`);
    }
  };

  return (
    <div className="repo-details-page">
      <h1>Repository: {repoName}</h1>
      <h2>Files</h2>
      <ul>
        {files.map((file, index) => (
          <li key={index}>{file}</li>
        ))}
      </ul>

      <div className="file-actions">
        <h2>Create New File</h2>
        <input
          type="text"
          placeholder="Enter file name (e.g., newfile.txt)"
          value={newFileName}
          onChange={(e) => setNewFileName(e.target.value)}
        />
        <button onClick={handleCreateFile}>Create File</button>

        <h2>Upload File</h2>
        <input type="file" onChange={handleFileUpload} />
      </div>

      {fileUploadMessage && <p className="upload-message">{fileUploadMessage}</p>}
    </div>
  );
};

export default RepoDetailsPage;
